import ReactDOM from 'react-dom/client'
import { useState } from 'react'

import Home from './components/Home'
import Workshops from './components/Workshops'
import Register from './components/Register'
import Login from './components/Login'
import Profile from './components/Profile'

import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  Navigate,
  useParams,
  useNavigate,
  useLocation
} from "react-router-dom"

const App = () => {

  const [workshops, setWorkshops] = useState([
    {
      id: 1,
      name: 'Domus',
      objective: 'Desarrollar procesos de investigación estudiantil, que soporten el enfoque del grupo Entorno Económico. Es decir, que expliquen el entorno local e internacional de las organizaciones, con el fin de promover acciones concretas que contribuyan al desarrollo económico y social de las sociedades.',
      research_group: 3
    }
  ])

  const [researchGroups, setResearchGroups] = useState([
    {
      id: 1,
      name: 'Entrepeneurship Group'
    },
    {
      id: 2,
      name: 'Grupo de gerencia en las grandes, pequeñas y medianas empresas -G3Pymes'
    },
    {
      id: 3,
      name: 'Entorno Económico de las organizaciones'
    },
    {
      id: 4,
      name: 'Dirección & gestión de proyectos'
    },
    {
      id: 5,
      name: 'Grupo de investigación y desarrollo Tecnológico ONTARE'
    },
    {
      id: 6,
      name: 'Gestión Ambiental'
    },
    {
      id: 7,
      name: 'Ciencias Básicas'
    },
    {
      id: 8,
      nam: 'GIS (Grupo de Investigación en Salud)'
    },
    {
      id: 9,
      name: 'INDEVOS'
    },
    {
      id: 10,
      name: 'Lingüística y comunicación Organizacional'
    },
    {
      id: 11,
      name: 'Política y Sostenibilidad'
    },
    {
      id: 12,
      name: 'Ambientes de Aprendizaje'
    }
  ])

  const padding = {
    padding: 5
  }

  const [loggedUser, setLoggedUser] = useState(null)
  const [users, setUsers] = useState([
    {
      id: 1,
      username: "velomeister",
      password: "velomeister",
      role: 1,
      workshop: 1
    }
  ])

  const login = (user) => {
    const tryingUser = users.find(validUser => validUser.username === user.username)
    if (tryingUser.password === user.password) {
      setLoggedUser(user)
    } else {
      <Navigate replace to="/login?error=1" />
    }
  }

  const register = (user) => {
    user.id = users.length + 1
    setUsers(users.concat(user))
  }

  function useQuery() {
    const { error } = useLocation();

    return React.useMemo(() => new URLSearchParams(error), [error]);
  }

  let query = useQuery()

  return (
    <Router>
      <div>
        <Link style={padding} to="/">Inicio</Link>
        <Link style={padding} to="/workshops">Semilleros</Link>
        <Link style={padding} to="/register">Registrarme</Link>
        <Link style={padding} to="/login">Iniciar Sesión</Link>
        <Link style={padding} to="/profile">Mi perfil</Link>
      </div>

      <Routes>
        <Route path="/workshops" element={<Workshops workshops={workshops} researchGroups={researchGroups} />} />
        <Route path="/register" element={<Register workshops={workshops} onRegister={register} />} />
        <Route path="/profile" element={loggedUser ? <Profile /> : <Navigate replace to="/login" />} />
        <Route path="/login" element={query.get(error) ? <Login onLogin={login} /> : <Login onLogin={login} errorMessage="Usuario o contraseña inválidos" />} />
        <Route path="/" element={<Home />} />
      </Routes>
    </Router>
  )
}



export default App
